package Basics;


public class DefaultValue {
	

	   int varInt;
	   float varFloat;
	   boolean varBool;
	   long varLong;
	   byte varByte;
	   short varShort;
	   double varDouble;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			
		DefaultValue value = new DefaultValue();
		
		System.out.println("Default Int value :"+value.varInt );
	    System.out.println("Default float value :"+value.varFloat);
	    System.out.println("Default boolean value :"+value.varBool);
	    System.out.println("Default long value :"+value.varLong);
	    System.out.println("Default byte value :"+value.varByte);
        System.out.println("Default short value :"+value.varShort);
        System.out.println("Default double value :"+value.varDouble);
		
		
	}
}